            className   = 'pdf-file'
            draggable
          >
            {/* <img src={FileUpload}></img> */}
            <p>
              Drag and drop files here <br/>
              or
            </p>
            <input 
              id='upload-ligand'
              type='file'
              multiple
              accept=".pdf"
              // onChange={(event) => this.uploadFiles(event.target.files)}
            >
