import React from "react"
import DropFileInput from "../components/drop-file-input/DropFileInput"
import MessageBox from "../components/messagebox/messagebox"
import "./home.css"
export default class Home extends React.Component{
  constructor(props) {
    super(props)
    this.state = {
      filesMsgBox: false,
      noFilesMsgBox: false,
      files_list: []
    }
  }
  uploadFiles = (files) =>{
    this.setState({files_list: files})
  }
  handleSubmit = () => {
    if(this.state.files_list.length > 0){
      this.setState({filesMsgBox: true})
    }
    else{
      this.setState({noFilesMsgBox: true})
    }
  }
  render(){
    return(
      <div className="box">
        <h2 className="header">
          Input Files
        </h2>
        <DropFileInput uploadFiles={this.uploadFiles}/>
        <button
          className="button"
          onClick={this.handleSubmit}
        >
          Submit Files
        </button>
        <MessageBox state={this.state.noFilesMsgBox}>
          <p>Please upload files first</p>
          <button 
            onClick={()=>this.setState({noFilesMsgBox: false})}
          >
            close
          </button>
        </MessageBox>
        <MessageBox state={this.state.filesMsgBox}>
          <p>Please upload files first</p>
          <button 
            onClick={()=>this.setState({filesMsgBox: false})}
          >
            close
          </button>
        </MessageBox>
      </div>
    )
  }
}
