import React from "react"
export default class Home extends React.Component{
  render(){
    return(
      <div className='protein-file'>
          <div
            // onDragOver  = {(event) => dragFunction(event, 'over')}
            // onDrop      = {(event) => dragFunction(event, 'drop')}
            // onDragEnter = {(event) => dragFunction(event, 'enter')}
            // onDragLeave = {(event) => dragFunction(event, 'leave')}
            className   = 'set-ligand-file'
            draggable
          >
            {/* <img src={FileUpload}></img> */}
            <p>
              Drag and drop files here <br/>
              or
            </p>
            <input 
              id='upload-ligand'
              type='file'
              multiple
              accept=".pdf"
              // onChange={(event) => this.uploadFiles(event.target.files)}
            >
            </input>
            <label htmlFor='upload-ligand'>Upload Ligand</label>
          </div>

          <div className='set-ligand-file-list'>
            <p className='file-list-title'>Uploaded PDF files</p>
            {this.state.files_list.length === 0 && <p className='file-list-empty'>No ligands are attached yet.</p>}
              {this.state.files_list.map((item, index) =>
                <div className='file-list-item' key={`${item.name}-${index}`}>
                  <p className='file-name'>{item.name}</p>
                  <button className='file-delete' onClick={()=>this.deleteFromFiles(item)}>X</button>
                </div>
              )}
          </div>
      </div>
    )
  }
}
