import React from "react"
export default class Home extends React.Component{
  render(){
    return(
      <div className='protein-file'>
          <div
            // onDragOver  = {(event) => dragFunction(event, 'over')}
            // onDrop      = {(event) => dragFunction(event, 'drop')}
            // onDragEnter = {(event) => dragFunction(event, 'enter')}
            // onDragLeave = {(event) => dragFunction(event, 'leave')}
            className   = 'set-ligand-file'
            draggable
          >
            <img src={FileUpload}></img>
            <p>
              Drag and drop files here <br/>
              or
            </p>
            <input 
              id='upload-ligand'
              type='file'
              multiple
              accept=".ent, .mdl, .mol, .mol2, .sd, .sdf, .pdb, .pdbqt,
                      .alc, .bgf, .c3d1, .c3d2, .crk3d, .dmol, .fract,
                      .gam, .gamin, .gamout, .gpr, .gzmat, .inp, .mdl,
                      .mdp, .nwo, .tmol, .xyz"
              onChange={(event) => this.uploadFiles(event.target.files)}
            >
            </input>
            <label htmlFor='upload-ligand'>Upload Ligand</label>
          </div>
      </div>
    )
  }
}
